import React from 'react';
import ProfileImg from '../Arash_Zakeresfahani.png';
import Footer from './Footer.js';


function Homepage(){
	return(
		<div className="container pt-5 mt-5 text-center" style={{maxWidth: "75%"}}>
      <section className="card border-0 mx-auto">

        <div className="row">
            <span className="col-md-4 mx-auto">
                    <div className="row">
                      <img
                      src= {ProfileImg}
                      className="card-img rounded-circle mx-auto pt-1" 
                      alt="Arash Zakeresfahani"
                      style={{maxWidth: "350px"}}/> 
                    </div>

            </span>
            <span className="col-md-8">
              <div className="card-body">
              <h3 class="card-title text-left">My name is Arash Zakeresfahani.</h3>
                <p className="card-text text-left"> 
                I am a third year Ph.D. student at Indiana University. I study Health Informatics and Human-Computer Interaction. I use a variety of quantitative and qualitative research methods to study communities of pregnancy care providers and identify healthcare resources in Indiana. My goal is to use human-center and participatory design to develop interactive technologies to improve access to pregnancy-related resourcess in marginalized and rural communities. The most enjoyable part of my work is interacting with my study participants and learning about their experiences.
                </p>
              </div>
            </span>
        </div>


      </section>
      <Footer />
    </div>
	);
}

export default Homepage;
