import * as d3 from "d3";
import * as topojson from "topojson-client";
import MapData from "../Census_County_TIGER00_IN.json";

const DrawMap = (caseType, data) => {
  console.log("drawing map");

  // setting up svg element on Home component; making size responsive
  const height = 500;
  const width = 960;

  const svg = d3
    .select(".mapviz")
    .append("svg")
    .attr("preserveAspectRatio", "xMinYMid meet")
    .attr("viewBox", [0, 0, width, height]);

  // specifying map projection
  const projection = d3.geoMercator();
  const pathGenerator = d3.geoPath().projection(projection);

  // creating a group to arrange map elements
  const g = svg.append("g").attr("class", "map-group");
  g.append("path")
    .attr("class", "sphere")
    .attr("d", pathGenerator({ type: "Sphere" }));

  // enabling zooming/panning on entire svg
  //   svg.call(
  //     d3.zoom().on("zoom", () => {
  //       g.attr("transform", d3.event.transform);
  //     })
  //   );

  // draws a path for each country with countryName as title (shown on hover)
  const countries = topojson.feature(
    MapData,
    MapData.objects.Census_County_TIGER00_IN1
  );
  console.log(MapData.objects.Census_County_TIGER00_IN1);
  g.selectAll("path")
    .data(countries.features)
    .enter()
    .append("path")
    .attr("class", "country")
    .attr("d", pathGenerator)
    .append("title");
};

export default DrawMap;
