import * as d3 from "d3";
import * as topojson from "topojson-client";
import MapData from "../Census_County_TIGER00_IN.json";

const DrawMap = (caseType, data) => {
  console.log("drawing map");
  console.log(MapData);
  // setting up svg element on Home component; making size responsive
  const height = 500;
  const width = 960;

  var projection = d3.geo
    .equirectangular()
    .scale(75)
    .translate([width / 2, height / 2])
    .rotate([-180, 0]);
  var path = d3.geo.path().projection(projection);
  var svg = d3
    .select("#js-map-nz-center")
    .append("svg")
    .attr("width", width)
    .attr("height", height);
  vg.selectAll(".graticule")
    .data([topojson.object(worldtopo, worldtopo.objects.land)])
    .enter()
    .append("path")
    .attr("class", "land")
    .attr("d", path);
};

export default DrawMap;
